const menuToggle=document.querySelector(".burger"),nav=document.querySelector(".menu"),navLink=document.querySelectorAll(".menu__link");function preloadImages(){["winter","spring","summer","autumn"].forEach((t=>{for(let e=1;e<=6;e++){(new Image).src=`./img/${t}/${e}.jpg`}}))}menuToggle.addEventListener("click",(()=>{nav.classList.toggle("opened"),menuToggle.classList.toggle("opened"),document.body.classList.toggle("lock")})),navLink.forEach((t=>t.addEventListener("click",(()=>{nav.classList.remove("opened"),menuToggle.classList.remove("opened"),document.body.classList.remove("lock")}))));const portfolioButtons=document.querySelector(".portfolio__buttons"),portfolioImages=document.querySelectorAll(".portfolio__image");function changePath(t){const e=t.dataset.season;portfolioImages.forEach(((t,o)=>{t.src=`./img/${e}/${o+1}.jpg`,t.alt=`${e} photography`}))}function changeButton(t){document.querySelector(".portfolio__button--active").classList.remove("portfolio__button--active"),t.classList.add("portfolio__button--active")}function changeImage(t){t.target.classList.contains("portfolio__button")&&!t.target.classList.contains("portfolio__button--active")&&(changePath(t.target),changeButton(t.target))}portfolioButtons.addEventListener("click",changeImage);